body.script:
$(document).ready(function () {
    const currentURI = window.location.pathname;
    console.log("현재 URI (경로):", currentURI);
    if (currentURI === "/") {
        $("#main-container").hide();
        $("#list-container").show();
        $(".back-button-container").empty(); // "이전" 버튼 컨테이너 비움
    } else {
        $("#main-container").show();
        $("#list-container").hide();

        $(".back-button-container").html(`

                arrow_back_ios
                이전

        `);
    }
});
